import React, { Component } from 'react'
// import './App.scss';
import Video from "../../Components/Video/Video";
import VideoTitle from "../../Components/VideoTitle/VideoTitle" ;
import VideoStats from "../../Components/VideoStats/VideoStats";
import VideoDescription from "../../Components/VideoDescription/VideoDescription";
import Comments from "../../Components/Comments/Comments";
import VideoList from '../../Components/VideoList/VideoList';
// import "./MainVideo.scss";
import axios from 'axios';

const apiKey = "?api_key=58b01201-9bc4-44e9-8026-c3e3e1711c36";
const url = "http://project-2-api.herokuapp.com/videos/";

export default class MainPage extends Component {
    state = {
        videos: PropT,
        videoDetails: []
   } 

   getSpecificVideoId(id) {
       axios.get('http://project-2-api.herokuapp.com/videos/'+ id +'?api_key=58b01201-9bc4-44e9-8026-c3e3e1711c36')
        .then((response) => {

            this.setState({
                videos:response.data,
        });
    });
   }

   getVideo = () => {
       if (this.props.match.params.videoId) {
           this.getSpecificVideoId(this.props.match.params.videoId);
       } else {
           axios.get('http://project-2-api.herokuapp.com/videos?api_key=58b01201-9bc4-44e9-8026-c3e3e1711c36')
            .then((response) => {
                this.getSpecificVideoId(response.data[1].id);
            });
       }
   };


   componentDidMount() {
       axios.get(url+apiKey)
        .then((response) => {
            this.setState({
                videoDetails:response.data,
            })
        })
        this.getVideo();
   }

   componentDidUpdate(prevProps) {
       if (this.props.match.params.videoId !== prevProps.match.params.videoId) {
           this.getVideo();
       }
   }

//    selectVideo = (videoId) => {

//     // const selectedVideo = VideosArray.find((video) => videoId === video.id);

//         this.setState({
//             videos:selectedVideo,
//         });
//    };

    render(){
        if (this.state.videos === {}) {
            return <main>Loading recipe...</main>
        }

    const filteredVideoList = this.state.videoDetails.filter(
    (video) => video.id !== this.state.videos.id)
    console.log(this.state.videos.comments)

    return (
        <div className="main-video">
            <Video videoImage={this.state.videos.image}/>
            <div className="comment-video-wrapper">
                <div className="main-video__text">
                    <VideoTitle title={this.state.videos.title}/>
                    <VideoStats likes={this.state.videos.likes} views={this.state.videos.views} channel={this.state.videos.channel} date={new Date(this.state.videos.timestamp).toLocaleDateString()}/>
                    <VideoDescription description={this.state.videos.description}/>
                    <Comments commentsArray={this.state.videos.comments}/>
                </div>
                <VideoList  videos={filteredVideoList}/>
            </div>
            </div>

    )
}}
