import React from 'react'
import CommentInputForm from '../CommentInputForm/CommentInputForm'
import CommentCard from '../CommentCard/CommentCard';
import "./Comments.scss";

export default function Comments({commentCounter, commentsArray, formattedCommentDate}) {
    // const commentDateMS = commentsArray.timestamp;
    // const commentDate = new Date(commentDateMS);
    // const formattedCommentDate = (commentDate.getMonth() + 1) + "/" + (commentDate.getDate()) + "/" +  commentDate.getFullYear();

    return (
        <div>

            <p className="comment__counter">{commentCounter} Comments</p><CommentInputForm />
            {commentsArray.map((comment) => (
               <CommentCard key={comment.id} name={comment.name} comment={comment.comment} date={formattedCommentDate}/> 
            ))}
        </div>
    )
}
